import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import { motion, AnimatePresence } from 'framer-motion';
import { dbPromise } from './db';

interface Task {
  id?: number;  // Make id optional to match IndexedDB schema
  title: string;
  description: string;
  status: 'not_started' | 'in_progress' | 'completed';
  labels: string[];
  createdAt: Date;
  updatedAt: Date;
}

interface Label {
  name: string;
  color: string;
}

const App: React.FC = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [tasks, setTasks] = useState<Task[]>([]);
  const [labels, setLabels] = useState<Label[]>([]);
  const [showSuccess, setShowSuccess] = useState(false);
  const [draggingTask, setDraggingTask] = useState<number | null>(null);

  useEffect(() => {
    const loadData = async () => {
      const db = await dbPromise;
      const loadedTasks = await db.getAll('tasks');
      const loadedLabels = await db.getAll('labels');
      setTasks(loadedTasks);
      setLabels(loadedLabels);
    };
    loadData();
  }, []);

  useEffect(() => {
    if (isDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [isDarkMode]);

  useEffect(() => {
    const allCompleted = tasks.length > 0 && tasks.every(task => task.status === 'completed');
    if (allCompleted) {
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 3000);
    }
  }, [tasks]);

  const handleDragStart = (taskId: number) => {
    setDraggingTask(taskId);
  };

  const handleDragEnd = async (result: any) => {
    setDraggingTask(null);
    if (!result.destination) return;

    const { source, destination } = result;
    const taskId = parseInt(result.draggableId);
    const newStatus = destination.droppableId as Task['status'];

    const updatedTasks = tasks.map(task =>
      task.id === taskId
        ? { ...task, status: newStatus, updatedAt: new Date() }
        : task
    );

    setTasks(updatedTasks);

    const db = await dbPromise;
    await db.put('tasks', {
      ...tasks.find(t => t.id === taskId)!,
      status: newStatus,
      updatedAt: new Date()
    });
  };

  return (
    <div className={`min-h-screen transition-colors duration-300 ${
      isDarkMode ? 'dark bg-background-dark text-text-dark' : 'bg-background-light text-text-light'
    }`}>
      <div className="container mx-auto px-4 py-8">
        <header className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold">Todo App</h1>
          <button
            onClick={() => setIsDarkMode(!isDarkMode)}
            className="p-2 rounded-lg bg-primary-light dark:bg-primary-dark text-white"
          >
            {isDarkMode ? '🌞' : '🌙'}
          </button>
        </header>

        <DragDropContext onDragEnd={handleDragEnd}>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {['not_started', 'in_progress', 'completed'].map((status) => (
              <Droppable key={status} droppableId={status}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    className="bg-white dark:bg-gray-800 rounded-lg p-4 shadow-lg"
                  >
                    <h2 className="text-xl font-semibold mb-4 capitalize">
                      {status.replace('_', ' ')}
                    </h2>
                    {tasks
                      .filter(task => task.status === status)
                      .map((task, index) => (
                        <motion.div
                          key={task.id}
                          initial={{ opacity: 0, y: 20 }}
                          animate={{ opacity: 1, y: 0 }}
                          exit={{ opacity: 0, y: -20 }}
                          className={`p-4 mb-2 rounded-lg ${
                            draggingTask === task.id ? 'animate-shake' : ''
                          }`}
                          style={{
                            backgroundColor: isDarkMode ? '#374151' : '#F3F4F6',
                          }}
                        >
                          <h3 className="font-medium">{task.title}</h3>
                          <p className="text-sm opacity-75">{task.description}</p>
                          <div className="flex flex-wrap gap-2 mt-2">
                            {task.labels.map(label => (
                              <span
                                key={label}
                                className="px-2 py-1 text-xs rounded-full"
                                style={{
                                  backgroundColor: labels.find(l => l.name === label)?.color || '#E5E7EB'
                                }}
                              >
                                {label}
                              </span>
                            ))}
                          </div>
                        </motion.div>
                      ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </DragDropContext>

        <AnimatePresence>
          {showSuccess && (
            <motion.div
              initial={{ opacity: 0, scale: 0.5 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.5 }}
              className="fixed bottom-4 right-4 bg-green-500 text-white px-6 py-3 rounded-lg shadow-lg"
            >
              おめでとうございます！
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default App;
